import{_ as a,c as i,o as e,ae as r}from"./chunks/framework.BQlYxExx.js";const _=JSON.parse('{"title":"📋 社区规范","description":"","frontmatter":{},"headers":[],"relativePath":"community/guidelines.md","filePath":"community/guidelines.md"}'),t={name:"community/guidelines.md"};function o(n,l,h,s,u,d){return e(),i("div",null,[...l[0]||(l[0]=[r('<h1 id="📋-社区规范" tabindex="-1">📋 <strong>社区规范</strong> <a class="header-anchor" href="#📋-社区规范" aria-label="Permalink to &quot;📋 **社区规范**&quot;">​</a></h1><p>为了维护一个健康、开放、友好的社区环境，我们制定了以下社区规范，希望所有社区成员共同遵守。</p><h2 id="🤝-核心价值观" tabindex="-1">🤝 <strong>核心价值观</strong> <a class="header-anchor" href="#🤝-核心价值观" aria-label="Permalink to &quot;🤝 **核心价值观**&quot;">​</a></h2><h3 id="_1-开放包容" tabindex="-1">1. 开放包容 <a class="header-anchor" href="#_1-开放包容" aria-label="Permalink to &quot;1. 开放包容&quot;">​</a></h3><ul><li>尊重不同观点和文化背景</li><li>欢迎来自不同领域的专家和爱好者</li><li>鼓励学术讨论和思想碰撞</li><li>不排斥新思想和新方法</li></ul><h3 id="_2-科学严谨" tabindex="-1">2. 科学严谨 <a class="header-anchor" href="#_2-科学严谨" aria-label="Permalink to &quot;2. 科学严谨&quot;">​</a></h3><ul><li>保持科学态度，尊重事实和证据</li><li>鼓励基于数据和逻辑的讨论</li><li>避免主观臆断和情绪化表达</li><li>接受并尊重不同的学术观点</li></ul><h3 id="_3-合作共赢" tabindex="-1">3. 合作共赢 <a class="header-anchor" href="#_3-合作共赢" aria-label="Permalink to &quot;3. 合作共赢&quot;">​</a></h3><ul><li>鼓励社区成员之间的合作</li><li>分享知识和资源</li><li>互相支持和帮助</li><li>共同推动IGT理论的发展</li></ul><h3 id="_4-诚信负责" tabindex="-1">4. 诚信负责 <a class="header-anchor" href="#_4-诚信负责" aria-label="Permalink to &quot;4. 诚信负责&quot;">​</a></h3><ul><li>遵守学术规范和伦理准则</li><li>尊重知识产权</li><li>对自己的言论负责</li><li>不发布虚假信息</li></ul><h2 id="📌-行为准则" tabindex="-1">📌 <strong>行为准则</strong> <a class="header-anchor" href="#📌-行为准则" aria-label="Permalink to &quot;📌 **行为准则**&quot;">​</a></h2><h3 id="_1-尊重他人" tabindex="-1">1. 尊重他人 <a class="header-anchor" href="#_1-尊重他人" aria-label="Permalink to &quot;1. 尊重他人&quot;">​</a></h3><ul><li>不进行人身攻击或辱骂</li><li>不歧视任何个人或群体</li><li>尊重他人的隐私</li><li>不泄露他人的个人信息</li></ul><h3 id="_2-理性讨论" tabindex="-1">2. 理性讨论 <a class="header-anchor" href="#_2-理性讨论" aria-label="Permalink to &quot;2. 理性讨论&quot;">​</a></h3><ul><li>基于事实和逻辑进行讨论</li><li>避免情绪化表达</li><li>不使用攻击性语言</li><li>接受不同观点</li></ul><h3 id="_3-保持专业性" tabindex="-1">3. 保持专业性 <a class="header-anchor" href="#_3-保持专业性" aria-label="Permalink to &quot;3. 保持专业性&quot;">​</a></h3><ul><li>讨论内容与IGT理论相关</li><li>保持学术严谨性</li><li>引用可靠来源</li><li>避免发布无关内容</li></ul><h3 id="_4-遵守法律法规" tabindex="-1">4. 遵守法律法规 <a class="header-anchor" href="#_4-遵守法律法规" aria-label="Permalink to &quot;4. 遵守法律法规&quot;">​</a></h3><ul><li>不发布违法违规内容</li><li>不传播有害信息</li><li>遵守知识产权法规</li><li>尊重他人的合法权益</li></ul><h2 id="🚫-禁止行为" tabindex="-1">🚫 <strong>禁止行为</strong> <a class="header-anchor" href="#🚫-禁止行为" aria-label="Permalink to &quot;🚫 **禁止行为**&quot;">​</a></h2><h3 id="_1-人身攻击" tabindex="-1">1. 人身攻击 <a class="header-anchor" href="#_1-人身攻击" aria-label="Permalink to &quot;1. 人身攻击&quot;">​</a></h3><ul><li>对个人进行辱骂、诽谤或威胁</li><li>恶意贬低他人的观点或贡献</li><li>进行人身攻击或人身威胁</li><li>发布侮辱性内容</li></ul><h3 id="_2-虚假信息" tabindex="-1">2. 虚假信息 <a class="header-anchor" href="#_2-虚假信息" aria-label="Permalink to &quot;2. 虚假信息&quot;">​</a></h3><ul><li>发布虚假或误导性信息</li><li>传播未经证实的谣言</li><li>伪造数据或证据</li><li>夸大或扭曲事实</li></ul><h3 id="_3-垃圾信息" tabindex="-1">3. 垃圾信息 <a class="header-anchor" href="#_3-垃圾信息" aria-label="Permalink to &quot;3. 垃圾信息&quot;">​</a></h3><ul><li>发布与IGT理论无关的内容</li><li>重复发布相同内容</li><li>发布广告或推广信息</li><li>发布恶意链接</li></ul><h3 id="_4-侵权行为" tabindex="-1">4. 侵权行为 <a class="header-anchor" href="#_4-侵权行为" aria-label="Permalink to &quot;4. 侵权行为&quot;">​</a></h3><ul><li>侵犯他人的知识产权</li><li>未经授权使用他人的作品</li><li>抄袭或剽窃他人的研究成果</li><li>侵犯他人的隐私</li></ul><h2 id="⚠️-违规处理" tabindex="-1">⚠️ <strong>违规处理</strong> <a class="header-anchor" href="#⚠️-违规处理" aria-label="Permalink to &quot;⚠️ **违规处理**&quot;">​</a></h2><h3 id="_1-处理流程" tabindex="-1">1. 处理流程 <a class="header-anchor" href="#_1-处理流程" aria-label="Permalink to &quot;1. 处理流程&quot;">​</a></h3><ul><li><strong>第一次违规</strong>：警告并删除违规内容</li><li><strong>第二次违规</strong>：限制发言权限</li><li><strong>第三次违规</strong>：暂时封禁账号</li><li><strong>严重违规</strong>：永久封禁账号</li></ul><h3 id="_2-申诉机制" tabindex="-1">2. 申诉机制 <a class="header-anchor" href="#_2-申诉机制" aria-label="Permalink to &quot;2. 申诉机制&quot;">​</a></h3><ul><li>如果您认为处理不当，可以提出申诉</li><li>发送邮件至contact@imeta.space</li><li>提供详细的申诉理由和证据</li><li>核心团队将在3个工作日内回复</li></ul><h2 id="📢-社区监督" tabindex="-1">📢 <strong>社区监督</strong> <a class="header-anchor" href="#📢-社区监督" aria-label="Permalink to &quot;📢 **社区监督**&quot;">​</a></h2><p>我们鼓励社区成员参与社区监督，包括：</p><ul><li>举报违规行为</li><li>提供改进建议</li><li>参与社区管理</li><li>维护社区秩序</li></ul><h2 id="📌-建议和反馈" tabindex="-1">📌 <strong>建议和反馈</strong> <a class="header-anchor" href="#📌-建议和反馈" aria-label="Permalink to &quot;📌 **建议和反馈**&quot;">​</a></h2><p>如果您对社区规范有任何建议或反馈，欢迎通过以下方式联系我们：</p><ul><li>在GitHub Discussions中发帖</li><li>在Discord社群中讨论</li><li>发送邮件至contact@imeta.space</li></ul><p>我们会定期审查和更新社区规范，确保其适应社区的发展和变化。</p><h2 id="🌟-共建美好社区" tabindex="-1">🌟 <strong>共建美好社区</strong> <a class="header-anchor" href="#🌟-共建美好社区" aria-label="Permalink to &quot;🌟 **共建美好社区**&quot;">​</a></h2><p>元衍智库的成功离不开每一位社区成员的贡献和支持。让我们共同遵守社区规范，营造一个开放、包容、科学、严谨的社区环境，共同推动IGT理论的发展和应用！</p>',43)])])}const b=a(t,[["render",o]]);export{_ as __pageData,b as default};
