<span class="line"><span style="color:#E1E4E8;">    freqs </span><span style="color:#F97583;">=</span><span style="color:#E1E4E8;"> np.fft.fftfreq(</span><span style="color:#79B8FF;">len</span><span style="color:#E1E4E8;">(readings))</span></span>
